mapper.select:
select b.tvb_id, b.title, writer, mod_at, read_count, poster, tv_title, reg_date, cmt_qty,
(select count(*) from tv_heart h where h.tvb_id = b.tvb_id) as heart
from (
	select tvb_id from tv_board
	where like_hate = 1

	order by tvb_id desc
	limit #{pageStart}, #{qty}
) a left join tv_board b
on a.tvb_id = b.tvb_id

select b.tvb_id, b.title, writer, mod_at, read_count, poster, tv_title, reg_date, cmt_qty,
(select count(*) from tv_heart h where h.tvb_id = b.tvb_id) as heart
from (
	select tvb_id from tv_board
	where like_hate = 0

	order by tvb_id desc
	limit #{pageStart}, #{qty}
) a left join tv_board b
on a.tvb_id = b.tvb_id

mapper.update:
update tv_board set
title = #{title}, content = #{content}, mod_at = now()
where tvb_id = #{tvbId}
