mapper.select:
select b.tvb_id, b.title, writer, mod_at, read_count, 
(select count(*) from tv_heart h where h.tvb_id = b.tvb_id) as heart
from (
	select tvb_id from tv_board
	where tvb_id > 0

	limit #{pageStart}, #{qty}
) a left join tv_board b
on a.tvb_id = b.tvb_id
where like_hate = 1
order by b.tvb_id desc

select b.tvb_id, b.title, writer, mod_at, read_count, 
(select count(*) from tv_heart h where h.tvb_id = b.tvb_id) as heart
from (
	select tvb_id from tv_board
	where tvb_id > 0

	limit #{pageStart}, #{qty}
) a left join tv_board b
on a.tvb_id = b.tvb_id
where like_hate = 0
order by b.tvb_id desc

mapper.update:
update tv_board set
title = #{title}, content = #{content}, mod_at = now()
where tvb_id = #{tvbId}
